    function bilibili() {
      let dom = document.getElementById('BV1zKVtzFEQH')
      fetch('https://bilibili.o0w0b.top/api/b?id=BV1zKVtzFEQH').then(res=>res.json()).then(data=>{
        dom.innerHTML = `



          11:10


          ${data.title}
          ${data.desc}

            ${data.view}
            ${data.like}
            ${data.coin}


            视频

            ${data.owner}


        `
      })
    }



