3;    cursor: url(&#x27;https://cdn.jsdelivr.net/gh/o0w0b/StaticFiles@main/cursors/PaperPlane_Cursor/15.cur&#x27;), auto;&#125;/* 悬停菜单栏时的鼠标指针 */#nav .site-page:hover &#123;    cursor: url(&#x27;https://cdn.jsdelivr.net/gh/o0w0b/StaticFiles@main/cursors/PaperPlane_Cursor/01.cur&#x27;), auto !important;&#125;/* 悬停滚动条的鼠标指针（只对Chrome有效） */::-webkit-scrollbar:hover &#123;    cursor: url(&#x27;https://cdn.jsdelivr.net/gh/o0w0b/StaticFiles@main/cursors/PaperPlane_Cursor/09.cur&#x27;), auto !important;&#125;/* 悬停评论按钮的鼠标指针（我使用的是Gitalk/Valine） */.switch-btn,.switch-btn:before,.gt-user-inner,.gt-ico,.gt-user-name,.gt-btn,.vbtn,svg,.at_button &#123;    cursor: url(&#x27;https://cdn.jsdelivr.net/gh/o0w0b/StaticFiles@main/cursors/PaperPlane_Cursor/15.cur&#x27;), auto !important;&#125;/* 悬停非链接文本时的鼠标指针 */h1,h2,h3,h4,h5,h6,#subtitle,.content,.line,.post-content p,.post-content li &#123;    cursor: url(&#x27;https://cdn.jsdelivr.net/gh/o0w0b/StaticFiles@main/cursors/PaperPlane_Cursor/06.cur&#x27;), auto;&#125;/* 悬停文章日期 */.post-meta-date:hover,.post-meta-date-created:hover &#123;    cursor: url(&#x27;https://cdn.jsdelivr.net/gh/o0w0b/StaticFiles@main/cursors/PaperPlane_Cursor/06.cur&#x27;), auto !important;&#125;


